package GUI;

import arthurRSA.*;
import java.math.BigInteger;

import arthurRSA.RSA;

public class HashPassword {
    public static String hashWithRSA(String word){
        publicKey key = 
        new publicKey(
        new BigInteger(""),
        new BigInteger(""));
        return RSA.encry(word, key.getN(), key.getE());
    }
}
